DeviceProperties.ListenerScriptPacketParser:
IF(INDEXOF(plug1topic, "shellyplus1pm-deviceid") = 0)
    ADDERROR("Set correct 'plug1topic' value in module variables. Topic format is 'shellyplus1pm-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> MQTT Settings -> Client ID.");
    RETURN(-1);
END

VAR json := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(json, "Topic");

IF(topic = plug1topic + "/status/switch:0")
    VAR payload := PARSEJSON(json, "Payload");
    internalTemp := PARSEJSON(payload, "temperature.tC");
    voltage := PARSEJSON(payload, "voltage");
    current := PARSEJSON(payload, "current");
    VAR errors := PARSEJSON(payload, "errors", TRUE);

    IF (!ISNULL(errors))
        VAR errCount := LENGTH(errors);
        VAR i := 0;
        VAR errorText := "";

        WHILE i < errCount
            IF LENGTH(errorText)
                errorText += ", ";
            END
            errorText := errorText + GETAT(errors, i);
            i += 1;
        LOOP

        IF LENGTH(errorText)
            ADDERROR(errorText);
        END
    END
END

IF(INDEXOF(plug1topic, "shellyplus1pm-deviceid") = 0)
    ADDERROR("Set correct 'plug1topic' value in module variables. Topic format is 'shellyplus1pm-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> MQTT Settings -> Client ID.");
    RETURN(-1);
END

VAR json := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(json, "Topic");


IF(topic = plug1topic + "/status/switch:0")
    VAR payload := PARSEJSON(json, "Payload");
    Ed := PARSEJSON(payload, "apower") / 1000.0;
    To := PARSEJSON(payload, "aenergy.total") / 1000.0;
END

IF(INDEXOF(plug1topic, "shellyplus1pm-deviceid") = 0)
    ADDERROR("Set correct 'plug1topic' value in module variables. Topic format is 'shellyplus1pm-<deviceid>'. The Device ID can be found by opening url 'http://shellyIpAddress', in Settings -> MQTT Settings -> Client ID.");
    RETURN(-1);
END

VAR json := TOSTRING(RECEIVEDBYTES);
VAR topic := PARSEJSON(json, "Topic");


IF(topic = plug1topic + "/status/switch:0")
    VAR payload := PARSEJSON(json, "Payload");
    St := PARSEJSON(payload, "output");
END
